<?php$route = '/arm_groups/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['arm_group_label'])){ $arm_group_label = $_GET['arm_group_label']; } else { $arm_group_label = '';}	if(isset($_GET['arm_group_type'])){ $arm_group_type = $_GET['arm_group_type']; } else { $arm_group_type = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}	if(isset($_GET['group_id'])){ $group_id = $_GET['group_id']; } else { $group_id = '';}	$Query = "SELECT * FROM arm_groups WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE arm_groups SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($arm_group_id))			{			$query .= "arm_group_id='" . mysql_real_escape_string($arm_group_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($arm_group_label))			{			$query .= "arm_group_label='" . mysql_real_escape_string($arm_group_label) . "'";			}		if(isset($arm_group_type))			{			$query .= "arm_group_type='" . mysql_real_escape_string($arm_group_type) . "'";			}		if(isset($description))			{			$query .= "description='" . mysql_real_escape_string($description) . "'";			}		if(isset($group_id))			{			$query .= "group_id='" . mysql_real_escape_string($group_id) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Arm_groups Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>