<?php$route = '/arm_groups/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['arm_group_label'])){ $arm_group_label = $_GET['arm_group_label']; } else { $arm_group_label = '';}	if(isset($_GET['arm_group_type'])){ $arm_group_type = $_GET['arm_group_type']; } else { $arm_group_type = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}	if(isset($_GET['group_id'])){ $group_id = $_GET['group_id']; } else { $group_id = '';}		$query = "INSERT INTO arm_groups(";		if(isset($arm_group_id)){ $query .= $arm_group_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($arm_group_label)){ $query .= $arm_group_label . ","; }		if(isset($arm_group_type)){ $query .= $arm_group_type . ","; }		if(isset($description)){ $query .= $description . ","; }		if(isset($group_id)){ $query .= $group_id . ","; }		$query .= ") VALUES(";		if(isset($arm_group_id)){ $query .= "'" . mysql_real_escape_string($arm_group_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($arm_group_label)){ $query .= "'" . mysql_real_escape_string($arm_group_label) . "',"; }		if(isset($arm_group_type)){ $query .= "'" . mysql_real_escape_string($arm_group_type) . "',"; }		if(isset($description)){ $query .= "'" . mysql_real_escape_string($description) . "',"; }		if(isset($group_id)){ $query .= "'" . mysql_real_escape_string($group_id) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Arm_groups Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>