<?php$route = '/authorities/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['authority_id'])){ $authority_id = $_GET['authority_id']; } else { $authority_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['authority'])){ $authority = $_GET['authority']; } else { $authority = '';}		$query = "INSERT INTO authorities(";		if(isset($authority_id)){ $query .= $authority_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($authority)){ $query .= $authority . ","; }		$query .= ") VALUES(";		if(isset($authority_id)){ $query .= "'" . mysql_real_escape_string($authority_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($authority)){ $query .= "'" . mysql_real_escape_string($authority) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Authorities Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>