<?php$route = '/condition_browse/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['mesh_condition_id'])){ $mesh_condition_id = $_GET['mesh_condition_id']; } else { $mesh_condition_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['mesh_term'])){ $mesh_term = $_GET['mesh_term']; } else { $mesh_term = '';}	$Query = "SELECT * FROM condition_browse WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE condition_browse SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($mesh_condition_id))			{			$query .= "mesh_condition_id='" . mysql_real_escape_string($mesh_condition_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($mesh_term))			{			$query .= "mesh_term='" . mysql_real_escape_string($mesh_term) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Condition_browse Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>