<?php$route = '/condition_browse/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['mesh_condition_id'])){ $mesh_condition_id = $_GET['mesh_condition_id']; } else { $mesh_condition_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['mesh_term'])){ $mesh_term = $_GET['mesh_term']; } else { $mesh_term = '';}		$query = "INSERT INTO condition_browse(";		if(isset($mesh_condition_id)){ $query .= $mesh_condition_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($mesh_term)){ $query .= $mesh_term . ","; }		$query .= ") VALUES(";		if(isset($mesh_condition_id)){ $query .= "'" . mysql_real_escape_string($mesh_condition_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($mesh_term)){ $query .= "'" . mysql_real_escape_string($mesh_term) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Condition_browse Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>