<?php$route = '/conditions/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['condition_id'])){ $condition_id = $_GET['condition_id']; } else { $condition_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['condition'])){ $condition = $_GET['condition']; } else { $condition = '';}		$query = "INSERT INTO conditions(";		if(isset($condition_id)){ $query .= $condition_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($condition)){ $query .= $condition . ","; }		$query .= ") VALUES(";		if(isset($condition_id)){ $query .= "'" . mysql_real_escape_string($condition_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($condition)){ $query .= "'" . mysql_real_escape_string($condition) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Conditions Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>