<?php$route = '/delayed_results/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['firstreceived_disposition_date'])){ $firstreceived_disposition_date = $_GET['firstreceived_disposition_date']; } else { $firstreceived_disposition_date = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['firstreceived_results_date'])){ $firstreceived_results_date = $_GET['firstreceived_results_date']; } else { $firstreceived_results_date = '';}	$Query = "SELECT * FROM delayed_results WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE delayed_results SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($firstreceived_disposition_date))			{			$query .= "firstreceived_disposition_date='" . mysql_real_escape_string($firstreceived_disposition_date) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($firstreceived_results_date))			{			$query .= "firstreceived_results_date='" . mysql_real_escape_string($firstreceived_results_date) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Delayed_results Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>