<?php$route = '/delayed_results/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['firstreceived_disposition_date'])){ $firstreceived_disposition_date = $_GET['firstreceived_disposition_date']; } else { $firstreceived_disposition_date = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['firstreceived_results_date'])){ $firstreceived_results_date = $_GET['firstreceived_results_date']; } else { $firstreceived_results_date = '';}		$query = "INSERT INTO delayed_results(";		if(isset($firstreceived_disposition_date)){ $query .= $firstreceived_disposition_date . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($firstreceived_results_date)){ $query .= $firstreceived_results_date . ","; }		$query .= ") VALUES(";		if(isset($firstreceived_disposition_date)){ $query .= "'" . mysql_real_escape_string($firstreceived_disposition_date) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($firstreceived_results_date)){ $query .= "'" . mysql_real_escape_string($firstreceived_results_date) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Delayed_results Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>