<?php$route = '/designs/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['design_id'])){ $design_id = $_GET['design_id']; } else { $design_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['design_name'])){ $design_name = $_GET['design_name']; } else { $design_name = '';}	if(isset($_GET['design_value'])){ $design_value = $_GET['design_value']; } else { $design_value = '';}	if(isset($_GET['masked_role'])){ $masked_role = $_GET['masked_role']; } else { $masked_role = '';}	$Query = "SELECT * FROM designs WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE designs SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($design_id))			{			$query .= "design_id='" . mysql_real_escape_string($design_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($design_name))			{			$query .= "design_name='" . mysql_real_escape_string($design_name) . "'";			}		if(isset($design_value))			{			$query .= "design_value='" . mysql_real_escape_string($design_value) . "'";			}		if(isset($masked_role))			{			$query .= "masked_role='" . mysql_real_escape_string($masked_role) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Designs Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>