<?php$route = '/designs/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['design_id'])){ $design_id = $_GET['design_id']; } else { $design_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['design_name'])){ $design_name = $_GET['design_name']; } else { $design_name = '';}	if(isset($_GET['design_value'])){ $design_value = $_GET['design_value']; } else { $design_value = '';}	if(isset($_GET['masked_role'])){ $masked_role = $_GET['masked_role']; } else { $masked_role = '';}		$query = "INSERT INTO designs(";		if(isset($design_id)){ $query .= $design_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($design_name)){ $query .= $design_name . ","; }		if(isset($design_value)){ $query .= $design_value . ","; }		if(isset($masked_role)){ $query .= $masked_role . ","; }		$query .= ") VALUES(";		if(isset($design_id)){ $query .= "'" . mysql_real_escape_string($design_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($design_name)){ $query .= "'" . mysql_real_escape_string($design_name) . "',"; }		if(isset($design_value)){ $query .= "'" . mysql_real_escape_string($design_value) . "',"; }		if(isset($masked_role)){ $query .= "'" . mysql_real_escape_string($masked_role) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Designs Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>