<?php$route = '/facilities/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM facilities WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM facilities";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$facility_id = $Database['facility_id'];		$nct_id = $Database['nct_id'];		$status = $Database['status'];		$facility_name = $Database['facility_name'];		$city = $Database['city'];		$state = $Database['state'];		$zip = $Database['zip'];		$country = $Database['country'];		$F = array();		$F['ID'] = $ID;		$F['facility_id'] = $facility_id;		$F['nct_id'] = $nct_id;		$F['status'] = $status;		$F['facility_name'] = $facility_name;		$F['city'] = $city;		$F['state'] = $state;		$F['zip'] = $zip;		$F['country'] = $country;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>