<?php$route = '/facilities/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['facility_id'])){ $facility_id = $_GET['facility_id']; } else { $facility_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['status'])){ $status = $_GET['status']; } else { $status = '';}	if(isset($_GET['facility_name'])){ $facility_name = $_GET['facility_name']; } else { $facility_name = '';}	if(isset($_GET['city'])){ $city = $_GET['city']; } else { $city = '';}	if(isset($_GET['state'])){ $state = $_GET['state']; } else { $state = '';}	if(isset($_GET['zip'])){ $zip = $_GET['zip']; } else { $zip = '';}	if(isset($_GET['country'])){ $country = $_GET['country']; } else { $country = '';}	$Query = "SELECT * FROM facilities WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE facilities SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($facility_id))			{			$query .= "facility_id='" . mysql_real_escape_string($facility_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($status))			{			$query .= "status='" . mysql_real_escape_string($status) . "'";			}		if(isset($facility_name))			{			$query .= "facility_name='" . mysql_real_escape_string($facility_name) . "'";			}		if(isset($city))			{			$query .= "city='" . mysql_real_escape_string($city) . "'";			}		if(isset($state))			{			$query .= "state='" . mysql_real_escape_string($state) . "'";			}		if(isset($zip))			{			$query .= "zip='" . mysql_real_escape_string($zip) . "'";			}		if(isset($country))			{			$query .= "country='" . mysql_real_escape_string($country) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Facilities Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>