<?php$route = '/facilities/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['facility_id'])){ $facility_id = $_GET['facility_id']; } else { $facility_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['status'])){ $status = $_GET['status']; } else { $status = '';}	if(isset($_GET['facility_name'])){ $facility_name = $_GET['facility_name']; } else { $facility_name = '';}	if(isset($_GET['city'])){ $city = $_GET['city']; } else { $city = '';}	if(isset($_GET['state'])){ $state = $_GET['state']; } else { $state = '';}	if(isset($_GET['zip'])){ $zip = $_GET['zip']; } else { $zip = '';}	if(isset($_GET['country'])){ $country = $_GET['country']; } else { $country = '';}		$query = "INSERT INTO facilities(";		if(isset($facility_id)){ $query .= $facility_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($status)){ $query .= $status . ","; }		if(isset($facility_name)){ $query .= $facility_name . ","; }		if(isset($city)){ $query .= $city . ","; }		if(isset($state)){ $query .= $state . ","; }		if(isset($zip)){ $query .= $zip . ","; }		if(isset($country)){ $query .= $country . ","; }		$query .= ") VALUES(";		if(isset($facility_id)){ $query .= "'" . mysql_real_escape_string($facility_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($status)){ $query .= "'" . mysql_real_escape_string($status) . "',"; }		if(isset($facility_name)){ $query .= "'" . mysql_real_escape_string($facility_name) . "',"; }		if(isset($city)){ $query .= "'" . mysql_real_escape_string($city) . "',"; }		if(isset($state)){ $query .= "'" . mysql_real_escape_string($state) . "',"; }		if(isset($zip)){ $query .= "'" . mysql_real_escape_string($zip) . "',"; }		if(isset($country)){ $query .= "'" . mysql_real_escape_string($country) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Facilities Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>