<?php$route = '/facility_contacts/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['facility_contact_id'])){ $facility_contact_id = $_GET['facility_contact_id']; } else { $facility_contact_id = '';}	if(isset($_GET['facility_id'])){ $facility_id = $_GET['facility_id']; } else { $facility_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['contact_type'])){ $contact_type = $_GET['contact_type']; } else { $contact_type = '';}	if(isset($_GET['name_degree'])){ $name_degree = $_GET['name_degree']; } else { $name_degree = '';}	if(isset($_GET['phone'])){ $phone = $_GET['phone']; } else { $phone = '';}	if(isset($_GET['phone_ext'])){ $phone_ext = $_GET['phone_ext']; } else { $phone_ext = '';}	if(isset($_GET['email'])){ $email = $_GET['email']; } else { $email = '';}	$Query = "SELECT * FROM facility_contacts WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE facility_contacts SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($facility_contact_id))			{			$query .= "facility_contact_id='" . mysql_real_escape_string($facility_contact_id) . "'";			}		if(isset($facility_id))			{			$query .= "facility_id='" . mysql_real_escape_string($facility_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($contact_type))			{			$query .= "contact_type='" . mysql_real_escape_string($contact_type) . "'";			}		if(isset($name_degree))			{			$query .= "name_degree='" . mysql_real_escape_string($name_degree) . "'";			}		if(isset($phone))			{			$query .= "phone='" . mysql_real_escape_string($phone) . "'";			}		if(isset($phone_ext))			{			$query .= "phone_ext='" . mysql_real_escape_string($phone_ext) . "'";			}		if(isset($email))			{			$query .= "email='" . mysql_real_escape_string($email) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Facility_contacts Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>