<?php$route = '/intervention_arm_groups/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM intervention_arm_groups WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$int_arm_group_id = $Database['int_arm_group_id'];		$intervention_id = $Database['intervention_id'];		$nct_id = $Database['nct_id'];		$arm_group_label = $Database['arm_group_label'];		$F = array();		$F['ID'] = $ID;		$F['int_arm_group_id'] = $int_arm_group_id;		$F['intervention_id'] = $intervention_id;		$F['nct_id'] = $nct_id;		$F['arm_group_label'] = $arm_group_label;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>