<?php$route = '/intervention_arm_groups/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['int_arm_group_id'])){ $int_arm_group_id = $_GET['int_arm_group_id']; } else { $int_arm_group_id = '';}	if(isset($_GET['intervention_id'])){ $intervention_id = $_GET['intervention_id']; } else { $intervention_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['arm_group_label'])){ $arm_group_label = $_GET['arm_group_label']; } else { $arm_group_label = '';}	$Query = "SELECT * FROM intervention_arm_groups WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE intervention_arm_groups SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($int_arm_group_id))			{			$query .= "int_arm_group_id='" . mysql_real_escape_string($int_arm_group_id) . "'";			}		if(isset($intervention_id))			{			$query .= "intervention_id='" . mysql_real_escape_string($intervention_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($arm_group_label))			{			$query .= "arm_group_label='" . mysql_real_escape_string($arm_group_label) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Intervention_arm_groups Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>