<?php$route = '/intervention_arm_groups/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['int_arm_group_id'])){ $int_arm_group_id = $_GET['int_arm_group_id']; } else { $int_arm_group_id = '';}	if(isset($_GET['intervention_id'])){ $intervention_id = $_GET['intervention_id']; } else { $intervention_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['arm_group_label'])){ $arm_group_label = $_GET['arm_group_label']; } else { $arm_group_label = '';}		$query = "INSERT INTO intervention_arm_groups(";		if(isset($int_arm_group_id)){ $query .= $int_arm_group_id . ","; }		if(isset($intervention_id)){ $query .= $intervention_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($arm_group_label)){ $query .= $arm_group_label . ","; }		$query .= ") VALUES(";		if(isset($int_arm_group_id)){ $query .= "'" . mysql_real_escape_string($int_arm_group_id) . "',"; }		if(isset($intervention_id)){ $query .= "'" . mysql_real_escape_string($intervention_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($arm_group_label)){ $query .= "'" . mysql_real_escape_string($arm_group_label) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Intervention_arm_groups Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>