<?php$route = '/intervention_other_names/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['int_other_name_id'])){ $int_other_name_id = $_GET['int_other_name_id']; } else { $int_other_name_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['intervention_id'])){ $intervention_id = $_GET['intervention_id']; } else { $intervention_id = '';}	if(isset($_GET['other_name'])){ $other_name = $_GET['other_name']; } else { $other_name = '';}	$Query = "SELECT * FROM intervention_other_names WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE intervention_other_names SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($int_other_name_id))			{			$query .= "int_other_name_id='" . mysql_real_escape_string($int_other_name_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($intervention_id))			{			$query .= "intervention_id='" . mysql_real_escape_string($intervention_id) . "'";			}		if(isset($other_name))			{			$query .= "other_name='" . mysql_real_escape_string($other_name) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Intervention_other_names Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>