<?php$route = '/interventions/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['intervention_id'])){ $intervention_id = $_GET['intervention_id']; } else { $intervention_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['intervention_type'])){ $intervention_type = $_GET['intervention_type']; } else { $intervention_type = '';}	if(isset($_GET['intervention_name'])){ $intervention_name = $_GET['intervention_name']; } else { $intervention_name = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}		$query = "INSERT INTO interventions(";		if(isset($intervention_id)){ $query .= $intervention_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($intervention_type)){ $query .= $intervention_type . ","; }		if(isset($intervention_name)){ $query .= $intervention_name . ","; }		if(isset($description)){ $query .= $description . ","; }		$query .= ") VALUES(";		if(isset($intervention_id)){ $query .= "'" . mysql_real_escape_string($intervention_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($intervention_type)){ $query .= "'" . mysql_real_escape_string($intervention_type) . "',"; }		if(isset($intervention_name)){ $query .= "'" . mysql_real_escape_string($intervention_name) . "',"; }		if(isset($description)){ $query .= "'" . mysql_real_escape_string($description) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Interventions Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>