<?php$route = '/investigators/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM investigators WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$investigator_id = $Database['investigator_id'];		$facility_id = $Database['facility_id'];		$nct_id = $Database['nct_id'];		$name_degree = $Database['name_degree'];		$role = $Database['role'];		$affiliation = $Database['affiliation'];		$F = array();		$F['ID'] = $ID;		$F['investigator_id'] = $investigator_id;		$F['facility_id'] = $facility_id;		$F['nct_id'] = $nct_id;		$F['name_degree'] = $name_degree;		$F['role'] = $role;		$F['affiliation'] = $affiliation;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>