<?php$route = '/investigators/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['investigator_id'])){ $investigator_id = $_GET['investigator_id']; } else { $investigator_id = '';}	if(isset($_GET['facility_id'])){ $facility_id = $_GET['facility_id']; } else { $facility_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['name_degree'])){ $name_degree = $_GET['name_degree']; } else { $name_degree = '';}	if(isset($_GET['role'])){ $role = $_GET['role']; } else { $role = '';}	if(isset($_GET['affiliation'])){ $affiliation = $_GET['affiliation']; } else { $affiliation = '';}		$query = "INSERT INTO investigators(";		if(isset($investigator_id)){ $query .= $investigator_id . ","; }		if(isset($facility_id)){ $query .= $facility_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($name_degree)){ $query .= $name_degree . ","; }		if(isset($role)){ $query .= $role . ","; }		if(isset($affiliation)){ $query .= $affiliation . ","; }		$query .= ") VALUES(";		if(isset($investigator_id)){ $query .= "'" . mysql_real_escape_string($investigator_id) . "',"; }		if(isset($facility_id)){ $query .= "'" . mysql_real_escape_string($facility_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($name_degree)){ $query .= "'" . mysql_real_escape_string($name_degree) . "',"; }		if(isset($role)){ $query .= "'" . mysql_real_escape_string($role) . "',"; }		if(isset($affiliation)){ $query .= "'" . mysql_real_escape_string($affiliation) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Investigators Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>