<?php$route = '/keywords/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['keyword_id'])){ $keyword_id = $_GET['keyword_id']; } else { $keyword_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['keyword'])){ $keyword = $_GET['keyword']; } else { $keyword = '';}		$query = "INSERT INTO keywords(";		if(isset($keyword_id)){ $query .= $keyword_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($keyword)){ $query .= $keyword . ","; }		$query .= ") VALUES(";		if(isset($keyword_id)){ $query .= "'" . mysql_real_escape_string($keyword_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($keyword)){ $query .= "'" . mysql_real_escape_string($keyword) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Keywords Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>