<?php$route = '/links/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['link_id'])){ $link_id = $_GET['link_id']; } else { $link_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['url'])){ $url = $_GET['url']; } else { $url = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}	$Query = "SELECT * FROM links WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE links SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($link_id))			{			$query .= "link_id='" . mysql_real_escape_string($link_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($url))			{			$query .= "url='" . mysql_real_escape_string($url) . "'";			}		if(isset($description))			{			$query .= "description='" . mysql_real_escape_string($description) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Links Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>