<?php$route = '/links/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['link_id'])){ $link_id = $_GET['link_id']; } else { $link_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['url'])){ $url = $_GET['url']; } else { $url = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}		$query = "INSERT INTO links(";		if(isset($link_id)){ $query .= $link_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($url)){ $query .= $url . ","; }		if(isset($description)){ $query .= $description . ","; }		$query .= ") VALUES(";		if(isset($link_id)){ $query .= "'" . mysql_real_escape_string($link_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($url)){ $query .= "'" . mysql_real_escape_string($url) . "',"; }		if(isset($description)){ $query .= "'" . mysql_real_escape_string($description) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Links Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>