<?php$route = '/location_countries/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['location_countries_id'])){ $location_countries_id = $_GET['location_countries_id']; } else { $location_countries_id = '';}	if(isset($_GET['country'])){ $country = $_GET['country']; } else { $country = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}		$query = "INSERT INTO location_countries(";		if(isset($location_countries_id)){ $query .= $location_countries_id . ","; }		if(isset($country)){ $query .= $country . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		$query .= ") VALUES(";		if(isset($location_countries_id)){ $query .= "'" . mysql_real_escape_string($location_countries_id) . "',"; }		if(isset($country)){ $query .= "'" . mysql_real_escape_string($country) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Location_countries Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>