<?php$route = '/mesh_thesaurus/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['mesh_id'])){ $mesh_id = $_GET['mesh_id']; } else { $mesh_id = '';}	if(isset($_GET['mesh_term'])){ $mesh_term = $_GET['mesh_term']; } else { $mesh_term = '';}	$Query = "SELECT * FROM mesh_thesaurus WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE mesh_thesaurus SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($mesh_id))			{			$query .= "mesh_id='" . mysql_real_escape_string($mesh_id) . "'";			}		if(isset($mesh_term))			{			$query .= "mesh_term='" . mysql_real_escape_string($mesh_term) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Mesh_thesaurus Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>