<?php$route = '/nct_aliases/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['nct_alias_id'])){ $nct_alias_id = $_GET['nct_alias_id']; } else { $nct_alias_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['nct_alias'])){ $nct_alias = $_GET['nct_alias']; } else { $nct_alias = '';}	$Query = "SELECT * FROM nct_aliases WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE nct_aliases SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($nct_alias_id))			{			$query .= "nct_alias_id='" . mysql_real_escape_string($nct_alias_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($nct_alias))			{			$query .= "nct_alias='" . mysql_real_escape_string($nct_alias) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Nct_aliases Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>