<?php$route = '/nct_aliases/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['nct_alias_id'])){ $nct_alias_id = $_GET['nct_alias_id']; } else { $nct_alias_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['nct_alias'])){ $nct_alias = $_GET['nct_alias']; } else { $nct_alias = '';}		$query = "INSERT INTO nct_aliases(";		if(isset($nct_alias_id)){ $query .= $nct_alias_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($nct_alias)){ $query .= $nct_alias . ","; }		$query .= ") VALUES(";		if(isset($nct_alias_id)){ $query .= "'" . mysql_real_escape_string($nct_alias_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($nct_alias)){ $query .= "'" . mysql_real_escape_string($nct_alias) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Nct_aliases Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>