<?php$route = '/overall_officials/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['overall_official_id'])){ $overall_official_id = $_GET['overall_official_id']; } else { $overall_official_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['roles'])){ $roles = $_GET['roles']; } else { $roles = '';}	if(isset($_GET['name_degree'])){ $name_degree = $_GET['name_degree']; } else { $name_degree = '';}	if(isset($_GET['affiliation'])){ $affiliation = $_GET['affiliation']; } else { $affiliation = '';}		$query = "INSERT INTO overall_officials(";		if(isset($overall_official_id)){ $query .= $overall_official_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($roles)){ $query .= $roles . ","; }		if(isset($name_degree)){ $query .= $name_degree . ","; }		if(isset($affiliation)){ $query .= $affiliation . ","; }		$query .= ") VALUES(";		if(isset($overall_official_id)){ $query .= "'" . mysql_real_escape_string($overall_official_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($roles)){ $query .= "'" . mysql_real_escape_string($roles) . "',"; }		if(isset($name_degree)){ $query .= "'" . mysql_real_escape_string($name_degree) . "',"; }		if(isset($affiliation)){ $query .= "'" . mysql_real_escape_string($affiliation) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Overall_officials Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>