<?php$route = '/removed_countries/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['removed_countries_id'])){ $removed_countries_id = $_GET['removed_countries_id']; } else { $removed_countries_id = '';}	if(isset($_GET['country'])){ $country = $_GET['country']; } else { $country = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	$Query = "SELECT * FROM removed_countries WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE removed_countries SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($removed_countries_id))			{			$query .= "removed_countries_id='" . mysql_real_escape_string($removed_countries_id) . "'";			}		if(isset($country))			{			$query .= "country='" . mysql_real_escape_string($country) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Removed_countries Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>