<?php$route = '/reported_event_ctgy_grp/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM reported_event_ctgy_grp WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$reported_event_catgy_grp_id = $Database['reported_event_catgy_grp_id'];		$reported_event_category_id = $Database['reported_event_category_id'];		$arm_group_id = $Database['arm_group_id'];		$subjects_affected = $Database['subjects_affected'];		$subjects_at_risk = $Database['subjects_at_risk'];		$events = $Database['events'];		$F = array();		$F['ID'] = $ID;		$F['reported_event_catgy_grp_id'] = $reported_event_catgy_grp_id;		$F['reported_event_category_id'] = $reported_event_category_id;		$F['arm_group_id'] = $arm_group_id;		$F['subjects_affected'] = $subjects_affected;		$F['subjects_at_risk'] = $subjects_at_risk;		$F['events'] = $events;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>