<?php$route = '/reported_event_ctgy_grp/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['reported_event_catgy_grp_id'])){ $reported_event_catgy_grp_id = $_GET['reported_event_catgy_grp_id']; } else { $reported_event_catgy_grp_id = '';}	if(isset($_GET['reported_event_category_id'])){ $reported_event_category_id = $_GET['reported_event_category_id']; } else { $reported_event_category_id = '';}	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}	if(isset($_GET['subjects_affected'])){ $subjects_affected = $_GET['subjects_affected']; } else { $subjects_affected = '';}	if(isset($_GET['subjects_at_risk'])){ $subjects_at_risk = $_GET['subjects_at_risk']; } else { $subjects_at_risk = '';}	if(isset($_GET['events'])){ $events = $_GET['events']; } else { $events = '';}	$Query = "SELECT * FROM reported_event_ctgy_grp WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE reported_event_ctgy_grp SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($reported_event_catgy_grp_id))			{			$query .= "reported_event_catgy_grp_id='" . mysql_real_escape_string($reported_event_catgy_grp_id) . "'";			}		if(isset($reported_event_category_id))			{			$query .= "reported_event_category_id='" . mysql_real_escape_string($reported_event_category_id) . "'";			}		if(isset($arm_group_id))			{			$query .= "arm_group_id='" . mysql_real_escape_string($arm_group_id) . "'";			}		if(isset($subjects_affected))			{			$query .= "subjects_affected='" . mysql_real_escape_string($subjects_affected) . "'";			}		if(isset($subjects_at_risk))			{			$query .= "subjects_at_risk='" . mysql_real_escape_string($subjects_at_risk) . "'";			}		if(isset($events))			{			$query .= "events='" . mysql_real_escape_string($events) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Reported_event_ctgy_grp Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>