<?php$route = '/responsible_parties/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM responsible_parties WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM responsible_parties";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$responsible_party_id = $Database['responsible_party_id'];		$nct_id = $Database['nct_id'];		$name_title = $Database['name_title'];		$organization = $Database['organization'];		$responsible_party_type = $Database['responsible_party_type'];		$investigator_affiliation = $Database['investigator_affiliation'];		$investigator_full_name = $Database['investigator_full_name'];		$investigator_title = $Database['investigator_title'];		$F = array();		$F['ID'] = $ID;		$F['responsible_party_id'] = $responsible_party_id;		$F['nct_id'] = $nct_id;		$F['name_title'] = $name_title;		$F['organization'] = $organization;		$F['responsible_party_type'] = $responsible_party_type;		$F['investigator_affiliation'] = $investigator_affiliation;		$F['investigator_full_name'] = $investigator_full_name;		$F['investigator_title'] = $investigator_title;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>