<?php$route = '/responsible_parties/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['responsible_party_id'])){ $responsible_party_id = $_GET['responsible_party_id']; } else { $responsible_party_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['name_title'])){ $name_title = $_GET['name_title']; } else { $name_title = '';}	if(isset($_GET['organization'])){ $organization = $_GET['organization']; } else { $organization = '';}	if(isset($_GET['responsible_party_type'])){ $responsible_party_type = $_GET['responsible_party_type']; } else { $responsible_party_type = '';}	if(isset($_GET['investigator_affiliation'])){ $investigator_affiliation = $_GET['investigator_affiliation']; } else { $investigator_affiliation = '';}	if(isset($_GET['investigator_full_name'])){ $investigator_full_name = $_GET['investigator_full_name']; } else { $investigator_full_name = '';}	if(isset($_GET['investigator_title'])){ $investigator_title = $_GET['investigator_title']; } else { $investigator_title = '';}		$query = "INSERT INTO responsible_parties(";		if(isset($responsible_party_id)){ $query .= $responsible_party_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($name_title)){ $query .= $name_title . ","; }		if(isset($organization)){ $query .= $organization . ","; }		if(isset($responsible_party_type)){ $query .= $responsible_party_type . ","; }		if(isset($investigator_affiliation)){ $query .= $investigator_affiliation . ","; }		if(isset($investigator_full_name)){ $query .= $investigator_full_name . ","; }		if(isset($investigator_title)){ $query .= $investigator_title . ","; }		$query .= ") VALUES(";		if(isset($responsible_party_id)){ $query .= "'" . mysql_real_escape_string($responsible_party_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($name_title)){ $query .= "'" . mysql_real_escape_string($name_title) . "',"; }		if(isset($organization)){ $query .= "'" . mysql_real_escape_string($organization) . "',"; }		if(isset($responsible_party_type)){ $query .= "'" . mysql_real_escape_string($responsible_party_type) . "',"; }		if(isset($investigator_affiliation)){ $query .= "'" . mysql_real_escape_string($investigator_affiliation) . "',"; }		if(isset($investigator_full_name)){ $query .= "'" . mysql_real_escape_string($investigator_full_name) . "',"; }		if(isset($investigator_title)){ $query .= "'" . mysql_real_escape_string($investigator_title) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Responsible_parties Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>