<?php$route = '/results_baseline/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['rslts_baseline_id'])){ $rslts_baseline_id = $_GET['rslts_baseline_id']; } else { $rslts_baseline_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['population'])){ $population = $_GET['population']; } else { $population = '';}	$Query = "SELECT * FROM results_baseline WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_baseline SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($rslts_baseline_id))			{			$query .= "rslts_baseline_id='" . mysql_real_escape_string($rslts_baseline_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($population))			{			$query .= "population='" . mysql_real_escape_string($population) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_baseline Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>