<?php$route = '/results_baseline_measure_catgy/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM results_baseline_measure_catgy WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$baseline_measure_catgy_id = $Database['baseline_measure_catgy_id'];		$baseline_id = $Database['baseline_id'];		$category_title = $Database['category_title'];		$arm_group_id = $Database['arm_group_id'];		$baseline_value = $Database['baseline_value'];		$spread = $Database['spread'];		$lower_limit = $Database['lower_limit'];		$upper_limit = $Database['upper_limit'];		$F = array();		$F['ID'] = $ID;		$F['baseline_measure_catgy_id'] = $baseline_measure_catgy_id;		$F['baseline_id'] = $baseline_id;		$F['category_title'] = $category_title;		$F['arm_group_id'] = $arm_group_id;		$F['baseline_value'] = $baseline_value;		$F['spread'] = $spread;		$F['lower_limit'] = $lower_limit;		$F['upper_limit'] = $upper_limit;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>