<?php$route = '/results_baseline_measure/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['baseline_id'])){ $baseline_id = $_GET['baseline_id']; } else { $baseline_id = '';}	if(isset($_GET['rslts_baseline_id'])){ $rslts_baseline_id = $_GET['rslts_baseline_id']; } else { $rslts_baseline_id = '';}	if(isset($_GET['baseline_measure_title'])){ $baseline_measure_title = $_GET['baseline_measure_title']; } else { $baseline_measure_title = '';}	if(isset($_GET['description'])){ $description = $_GET['description']; } else { $description = '';}	if(isset($_GET['units_of_measure'])){ $units_of_measure = $_GET['units_of_measure']; } else { $units_of_measure = '';}	if(isset($_GET['measure_type'])){ $measure_type = $_GET['measure_type']; } else { $measure_type = '';}	if(isset($_GET['dispersion'])){ $dispersion = $_GET['dispersion']; } else { $dispersion = '';}	$Query = "SELECT * FROM results_baseline_measure WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_baseline_measure SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($baseline_id))			{			$query .= "baseline_id='" . mysql_real_escape_string($baseline_id) . "'";			}		if(isset($rslts_baseline_id))			{			$query .= "rslts_baseline_id='" . mysql_real_escape_string($rslts_baseline_id) . "'";			}		if(isset($baseline_measure_title))			{			$query .= "baseline_measure_title='" . mysql_real_escape_string($baseline_measure_title) . "'";			}		if(isset($description))			{			$query .= "description='" . mysql_real_escape_string($description) . "'";			}		if(isset($units_of_measure))			{			$query .= "units_of_measure='" . mysql_real_escape_string($units_of_measure) . "'";			}		if(isset($measure_type))			{			$query .= "measure_type='" . mysql_real_escape_string($measure_type) . "'";			}		if(isset($dispersion))			{			$query .= "dispersion='" . mysql_real_escape_string($dispersion) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_baseline_measure Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>