<?php$route = '/results_baseline/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['rslts_baseline_id'])){ $rslts_baseline_id = $_GET['rslts_baseline_id']; } else { $rslts_baseline_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['population'])){ $population = $_GET['population']; } else { $population = '';}		$query = "INSERT INTO results_baseline(";		if(isset($rslts_baseline_id)){ $query .= $rslts_baseline_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($population)){ $query .= $population . ","; }		$query .= ") VALUES(";		if(isset($rslts_baseline_id)){ $query .= "'" . mysql_real_escape_string($rslts_baseline_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($population)){ $query .= "'" . mysql_real_escape_string($population) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_baseline Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>