<?php$route = '/results_outcome_analysis_grp/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['results_outcome_anal_grp_id'])){ $results_outcome_anal_grp_id = $_GET['results_outcome_anal_grp_id']; } else { $results_outcome_anal_grp_id = '';}	if(isset($_GET['results_outcome_analysis_id'])){ $results_outcome_analysis_id = $_GET['results_outcome_analysis_id']; } else { $results_outcome_analysis_id = '';}	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}	$Query = "SELECT * FROM results_outcome_analysis_grp WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_outcome_analysis_grp SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($results_outcome_anal_grp_id))			{			$query .= "results_outcome_anal_grp_id='" . mysql_real_escape_string($results_outcome_anal_grp_id) . "'";			}		if(isset($results_outcome_analysis_id))			{			$query .= "results_outcome_analysis_id='" . mysql_real_escape_string($results_outcome_analysis_id) . "'";			}		if(isset($arm_group_id))			{			$query .= "arm_group_id='" . mysql_real_escape_string($arm_group_id) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_outcome_analysis_grp Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>