<?php$route = '/results_outcome_analysis_grp/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['results_outcome_anal_grp_id'])){ $results_outcome_anal_grp_id = $_GET['results_outcome_anal_grp_id']; } else { $results_outcome_anal_grp_id = '';}	if(isset($_GET['results_outcome_analysis_id'])){ $results_outcome_analysis_id = $_GET['results_outcome_analysis_id']; } else { $results_outcome_analysis_id = '';}	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}		$query = "INSERT INTO results_outcome_analysis_grp(";		if(isset($results_outcome_anal_grp_id)){ $query .= $results_outcome_anal_grp_id . ","; }		if(isset($results_outcome_analysis_id)){ $query .= $results_outcome_analysis_id . ","; }		if(isset($arm_group_id)){ $query .= $arm_group_id . ","; }		$query .= ") VALUES(";		if(isset($results_outcome_anal_grp_id)){ $query .= "'" . mysql_real_escape_string($results_outcome_anal_grp_id) . "',"; }		if(isset($results_outcome_analysis_id)){ $query .= "'" . mysql_real_escape_string($results_outcome_analysis_id) . "',"; }		if(isset($arm_group_id)){ $query .= "'" . mysql_real_escape_string($arm_group_id) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_outcome_analysis_grp Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>