<?php$route = '/results_outcome_measure_ctgy/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM results_outcome_measure_ctgy WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM results_outcome_measure_ctgy";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$outcome_measure_catgy_id = $Database['outcome_measure_catgy_id'];		$outcome_measure_id = $Database['outcome_measure_id'];		$category_title = $Database['category_title'];		$arm_group_id = $Database['arm_group_id'];		$outcome_value = $Database['outcome_value'];		$spread = $Database['spread'];		$lower_limit = $Database['lower_limit'];		$upper_limit = $Database['upper_limit'];		$F = array();		$F['ID'] = $ID;		$F['outcome_measure_catgy_id'] = $outcome_measure_catgy_id;		$F['outcome_measure_id'] = $outcome_measure_id;		$F['category_title'] = $category_title;		$F['arm_group_id'] = $arm_group_id;		$F['outcome_value'] = $outcome_value;		$F['spread'] = $spread;		$F['lower_limit'] = $lower_limit;		$F['upper_limit'] = $upper_limit;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>