<?php$route = '/results_outcome_measure/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM results_outcome_measure WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM results_outcome_measure";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$outcome_measure_id = $Database['outcome_measure_id'];		$outcome_id = $Database['outcome_id'];		$outcome_measure_title = $Database['outcome_measure_title'];		$measure_description = $Database['measure_description'];		$unit_of_measure = $Database['unit_of_measure'];		$measure_type = $Database['measure_type'];		$dispersion = $Database['dispersion'];		$F = array();		$F['ID'] = $ID;		$F['outcome_measure_id'] = $outcome_measure_id;		$F['outcome_id'] = $outcome_id;		$F['outcome_measure_title'] = $outcome_measure_title;		$F['measure_description'] = $measure_description;		$F['unit_of_measure'] = $unit_of_measure;		$F['measure_type'] = $measure_type;		$F['dispersion'] = $dispersion;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>