<?php$route = '/results_outcomes/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM results_outcomes WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM results_outcomes";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$outcome_id = $Database['outcome_id'];		$nct_id = $Database['nct_id'];		$outcome_type = $Database['outcome_type'];		$outcome_title = $Database['outcome_title'];		$time_frame = $Database['time_frame'];		$safety_issue = $Database['safety_issue'];		$outcome_description = $Database['outcome_description'];		$population = $Database['population'];		$posting_date = $Database['posting_date'];		$F = array();		$F['ID'] = $ID;		$F['outcome_id'] = $outcome_id;		$F['nct_id'] = $nct_id;		$F['outcome_type'] = $outcome_type;		$F['outcome_title'] = $outcome_title;		$F['time_frame'] = $time_frame;		$F['safety_issue'] = $safety_issue;		$F['outcome_description'] = $outcome_description;		$F['population'] = $population;		$F['posting_date'] = $posting_date;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>