<?php$route = '/results_partflow_mlstn_grp/';$app->get($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['query'])){ $query = $_GET['query']; } else { $query = '';}	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Where = "ID IS NOT NULL";	if($in != '')		{		$inArray = explode(',',$in);		foreach($inArray as $item)			{			$Where .= " OR " . $item . " LIKE '%" . $query . "%'";			}		}	if($query=='')		{		$Query = "SELECT * FROM results_partflow_mlstn_grp WHERE " . $Where ;		}	else		{		$Query = "SELECT * FROM results_partflow_mlstn_grp";		}	if($sort!='')		{		$Query .= " ORDER BY " . $sort . " ASC";		}	$Query .= " LIMIT " . $page . "," . $count;	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$milestone_group_id = $Database['milestone_group_id'];		$milestone_id = $Database['milestone_id'];		$arm_group_id = $Database['arm_group_id'];		$partflow_count = $Database['partflow_count'];		$participant_description = $Database['participant_description'];		$F = array();		$F['ID'] = $ID;		$F['milestone_group_id'] = $milestone_group_id;		$F['milestone_id'] = $milestone_id;		$F['arm_group_id'] = $arm_group_id;		$F['partflow_count'] = $partflow_count;		$F['participant_description'] = $participant_description;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>