<?php$route = '/results_partflow_mlstn_grp/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['milestone_group_id'])){ $milestone_group_id = $_GET['milestone_group_id']; } else { $milestone_group_id = '';}	if(isset($_GET['milestone_id'])){ $milestone_id = $_GET['milestone_id']; } else { $milestone_id = '';}	if(isset($_GET['arm_group_id'])){ $arm_group_id = $_GET['arm_group_id']; } else { $arm_group_id = '';}	if(isset($_GET['partflow_count'])){ $partflow_count = $_GET['partflow_count']; } else { $partflow_count = '';}	if(isset($_GET['participant_description'])){ $participant_description = $_GET['participant_description']; } else { $participant_description = '';}	$Query = "SELECT * FROM results_partflow_mlstn_grp WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_partflow_mlstn_grp SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($milestone_group_id))			{			$query .= "milestone_group_id='" . mysql_real_escape_string($milestone_group_id) . "'";			}		if(isset($milestone_id))			{			$query .= "milestone_id='" . mysql_real_escape_string($milestone_id) . "'";			}		if(isset($arm_group_id))			{			$query .= "arm_group_id='" . mysql_real_escape_string($arm_group_id) . "'";			}		if(isset($partflow_count))			{			$query .= "partflow_count='" . mysql_real_escape_string($partflow_count) . "'";			}		if(isset($participant_description))			{			$query .= "participant_description='" . mysql_real_escape_string($participant_description) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_partflow_mlstn_grp Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>