<?php$route = '/results_partflow_mlstn/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM results_partflow_mlstn WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$milestone_id = $Database['milestone_id'];		$participant_flow_id = $Database['participant_flow_id'];		$milestone_type = $Database['milestone_type'];		$milestone_title = $Database['milestone_title'];		$F = array();		$F['ID'] = $ID;		$F['milestone_id'] = $milestone_id;		$F['participant_flow_id'] = $participant_flow_id;		$F['milestone_type'] = $milestone_type;		$F['milestone_title'] = $milestone_title;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>