<?php$route = '/results_partflow_mlstn/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['milestone_id'])){ $milestone_id = $_GET['milestone_id']; } else { $milestone_id = '';}	if(isset($_GET['participant_flow_id'])){ $participant_flow_id = $_GET['participant_flow_id']; } else { $participant_flow_id = '';}	if(isset($_GET['milestone_type'])){ $milestone_type = $_GET['milestone_type']; } else { $milestone_type = '';}	if(isset($_GET['milestone_title'])){ $milestone_title = $_GET['milestone_title']; } else { $milestone_title = '';}	$Query = "SELECT * FROM results_partflow_mlstn WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_partflow_mlstn SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($milestone_id))			{			$query .= "milestone_id='" . mysql_real_escape_string($milestone_id) . "'";			}		if(isset($participant_flow_id))			{			$query .= "participant_flow_id='" . mysql_real_escape_string($participant_flow_id) . "'";			}		if(isset($milestone_type))			{			$query .= "milestone_type='" . mysql_real_escape_string($milestone_type) . "'";			}		if(isset($milestone_title))			{			$query .= "milestone_title='" . mysql_real_escape_string($milestone_title) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_partflow_mlstn Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>