<?php$route = '/results_partic_flows/:id/';$app->get($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['sort'])){ $sort = $_GET['sort']; } else { $sort = '';}	if(isset($_GET['page'])){ $page = $_GET['page']; } else { $page = 0;}	if(isset($_GET['count'])){ $count = $_GET['count']; } else { $count = 25;}	if(isset($_GET['in'])){ $in = $_GET['in']; } else { $in = '';}	$ReturnObject = array();	$Query = "SELECT * FROM results_partic_flows WHERE id = '" . $id . "'";	$DatabaseResult = mysql_query($Query) or die('Query failed: ' . mysql_error());	while ($Database = mysql_fetch_assoc($DatabaseResult))		{		$ID = $Database['ID'];		$participant_flow_id = $Database['participant_flow_id'];		$nct_id = $Database['nct_id'];		$period_title = $Database['period_title'];		$recruitment_details = $Database['recruitment_details'];		$pre_assignment_details = $Database['pre_assignment_details'];		$F = array();		$F['ID'] = $ID;		$F['participant_flow_id'] = $participant_flow_id;		$F['nct_id'] = $nct_id;		$F['period_title'] = $period_title;		$F['recruitment_details'] = $recruitment_details;		$F['pre_assignment_details'] = $pre_assignment_details;		array_push($ReturnObject, $F);		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>