<?php$route = '/results_partic_flows/:id/';$app->put($route, function ($id)  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['participant_flow_id'])){ $participant_flow_id = $_GET['participant_flow_id']; } else { $participant_flow_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['period_title'])){ $period_title = $_GET['period_title']; } else { $period_title = '';}	if(isset($_GET['recruitment_details'])){ $recruitment_details = $_GET['recruitment_details']; } else { $recruitment_details = '';}	if(isset($_GET['pre_assignment_details'])){ $pre_assignment_details = $_GET['pre_assignment_details']; } else { $pre_assignment_details = '';}	$Query = "SELECT * FROM results_partic_flows WHERE id = '" . $id . "'";	$Database = mysql_query($Query) or die('Query failed: ' . mysql_error());	if($Database && mysql_num_rows($Database))		{		$query = "UPDATE results_partic_flows SET";		if(isset($id))			{			$query .= "id='" . mysql_real_escape_string($id) . "'";			}		if(isset($participant_flow_id))			{			$query .= "participant_flow_id='" . mysql_real_escape_string($participant_flow_id) . "'";			}		if(isset($nct_id))			{			$query .= "nct_id='" . mysql_real_escape_string($nct_id) . "'";			}		if(isset($period_title))			{			$query .= "period_title='" . mysql_real_escape_string($period_title) . "'";			}		if(isset($recruitment_details))			{			$query .= "recruitment_details='" . mysql_real_escape_string($recruitment_details) . "'";			}		if(isset($pre_assignment_details))			{			$query .= "pre_assignment_details='" . mysql_real_escape_string($pre_assignment_details) . "'";			}		$query .= " WHERE id = '" . $id . "'";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_partic_flows Updated!";		}	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>