<?php$route = '/results_partic_flows/';$app->post($route, function ()  use ($app){	$request = $app->request();	$_GET = $request->params();	if(isset($_GET['participant_flow_id'])){ $participant_flow_id = $_GET['participant_flow_id']; } else { $participant_flow_id = '';}	if(isset($_GET['nct_id'])){ $nct_id = $_GET['nct_id']; } else { $nct_id = '';}	if(isset($_GET['period_title'])){ $period_title = $_GET['period_title']; } else { $period_title = '';}	if(isset($_GET['recruitment_details'])){ $recruitment_details = $_GET['recruitment_details']; } else { $recruitment_details = '';}	if(isset($_GET['pre_assignment_details'])){ $pre_assignment_details = $_GET['pre_assignment_details']; } else { $pre_assignment_details = '';}		$query = "INSERT INTO results_partic_flows(";		if(isset($participant_flow_id)){ $query .= $participant_flow_id . ","; }		if(isset($nct_id)){ $query .= $nct_id . ","; }		if(isset($period_title)){ $query .= $period_title . ","; }		if(isset($recruitment_details)){ $query .= $recruitment_details . ","; }		if(isset($pre_assignment_details)){ $query .= $pre_assignment_details . ","; }		$query .= ") VALUES(";		if(isset($participant_flow_id)){ $query .= "'" . mysql_real_escape_string($participant_flow_id) . "',"; }		if(isset($nct_id)){ $query .= "'" . mysql_real_escape_string($nct_id) . "',"; }		if(isset($period_title)){ $query .= "'" . mysql_real_escape_string($period_title) . "',"; }		if(isset($recruitment_details)){ $query .= "'" . mysql_real_escape_string($recruitment_details) . "',"; }		if(isset($pre_assignment_details)){ $query .= "'" . mysql_real_escape_string($pre_assignment_details) . "',"; }		$query .= ")";		mysql_query($query) or die('Query failed: ' . mysql_error());		$ReturnObject = array();		$ReturnObject['message'] = "Results_partic_flows Added!";	$app->response()->header("Content-Type", "application/json");	echo stripslashes(format_json(json_encode($ReturnObject)));	});?>